#include <iostream>
#include <vector>

using namespace std;

template <typename T>
void pr(vector<T> x)
{
    for (auto a : x)
    {
        cout << a;
    }
    cout << endl;
}
class Solution
{
public:
    int chalkReplacer(vector<int> &chalk, int k)
    {
        long long pre[chalk.size()];
        pre[0] = chalk[0];
        for (int i = 1; i < chalk.size(); ++i)
        {
            pre[i] = pre[i - 1] + chalk[i];
        }
        k = k % pre[chalk.size() - 1];
        int l{0};
        int r = chalk.size() - 1;
        int mid;
        while (l < r)
        {
            mid = (l) + (r - l) / 2;
            if (pre[mid] > k)
            {
                r = mid;
            }
            else
            {
                l = mid + 1;
            }
        }
        return l;
    }
};

